scriptedOperation.script:
function run(input, params) {
  var importProperties = {};
  for (var property in params.properties) {
       if (property.substr(0, 4) == "flv:") {
    		importProperties[property] = params.properties[property];
       }
  }
  for (var property in importProperties) {
     delete params.properties[property];
  }
  var newDoc = Document.Create(input, params);
  return newDoc;
}

function run(input, params) {


  	// When republishing juggle states from "Republished" to "Publish" so publish listener is activated
        if (input.lifeCycle == 'Published') {
	  input = Document.FollowLifecycleTransition(input, { 'value': 'Republish' });
          return  Document.FollowLifecycleTransition(input, { 'value': 'Publish' });
        }
  	// When publishing for the first time, follow formal transition
          return Document.FollowLifecycleTransition(input, { 'value': 'Publish' });
}

function run(input, params) {
	var attached_words = input.getPropertyValue('fvm:attached_words');

  	if (  attached_words !== undefined && attached_words !== null && attached_words.length > 0) {

      		var index;
      		var attachedWord;
      		var wordID;

      		for (index = 0; index < attached_words.length; ++index) {

              		wordID = attached_words[index];

              		attachedWord = Repository.GetDocument(null, { 'value': wordID});

              		if (attachedWord !== undefined && attachedWord !== null) {
                      		DocumentMultivaluedProperty.addItem(attachedWord, { 'value': input.id, 'xpath': 'fv:related_pictures', 'checkExists': true, 'save': true });
                	}
            	}
  	}
}

