                case '22': Document.SetProperty(input, { 'xpath': "fv-word:part_of_speech", 'save': true, 'value': "particle_auxiliary_verb" }); break;
                case '23': Document.SetProperty(input, { 'xpath': "fv-word:part_of_speech", 'save': true, 'value': "particle_adjective" }); break;
                case '24': Document.SetProperty(input, { 'xpath': "fv-word:part_of_speech", 'save': true, 'value': "particle_adverb" }); break;
                case '25': Document.SetProperty(input, { 'xpath': "fv-word:part_of_speech", 'save': true, 'value': "entity_noun_like_word" }); break;
                case '26': Document.SetProperty(input, { 'xpath': "fv-word:part_of_speech", 'save': true, 'value': "event_activity_verb_like_word" }); break;
                case '27': Document.SetProperty(input, { 'xpath': "fv-word:part_of_speech", 'save': true, 'value': "event_activity_verb_like_word_transitive" }); break;
                case '28': Document.SetProperty(input, { 'xpath': "fv-word:part_of_speech", 'save': true, 'value': "event_activity_verb_like_word_intransitive" }); break;
                case '29': Document.SetProperty(input, { 'xpath': "fv-word:part_of_speech", 'save': true, 'value': "event_activity_verb_like_word_reflexive" }); break;
                case '30': Document.SetProperty(input, { 'xpath': "fv-word:part_of_speech", 'save': true, 'value': "event_activity_verb_like_word_reciprocal" }); break;
                case '31': Document.SetProperty(input, { 'xpath': "fv-word:part_of_speech", 'save': true, 'value': "question_word" }); break;
                case '32': Document.SetProperty(input, { 'xpath': "fv-word:part_of_speech", 'save': true, 'value': "suffix_prefix" }); break;
        	/* TODO: add the rest... */
      		default: Log(input, { 'level': 'error', 'message': 'Could not retrieve part of speech for ' + input.getPropertyValue('dc:title') }); break;
	}

	// Get the v1_id of the image referenced by this word
	var relatedPictureId = input.getPropertyValue('fv:related_pictures');

	if(relatedPictureId !== undefined && relatedPictureId !== null) {

    		// Try to retrieve the FVPicture document that has the v1_id
  		var relatedPictureDocuments = Document.FetchByProperty(input, { 'property': 'fvm:v1_id', 'values': relatedPictureId });

                if(relatedPictureDocuments.length === 0) {
                	Log(input, { 'level': 'error', 'message': 'Could not retrieve referenced image in ' + input.getPropertyValue('dc:title') });
                } else {
                        for(var i=0;i<relatedPictureDocuments.length;i++) {
                        	var fvPicture = relatedPictureDocuments[i];
                                if (fvPicture !== undefined && fvPicture !== null) {
                                        // Clear out the fv:related_pictures list in the FVWord, then add the Nuxeo id of the targeted FVPicture
                                        Document.RemoveProperty(input, { 'xpath': 'fv:related_pictures', 'save': true });
                                        DocumentMultivaluedProperty.addItem(input, { 'value': fvPicture.id, 'xpath': 'fv:related_pictures', 'checkExists': true, 'save': true });
                                        Log(input, { 'level': 'warn', 'message': 'Replaced image in ' + input.getPropertyValue('dc:title') });
                                }
                        }
                }
	}
  	return input;
}

function run(input, params) {
  	var dialect = Document.GetParent(input, {'type': 'INDialect'});

  	if (dialect !== undefined && dialect !== null) {
    		input.setPropertyValue('fva:dialect', dialect.id);
  	}
}

function run(input, params) {
  /**
  Will duplicate the document property
  **/
  var variations = input.getPropertyValue("inmorphem:kqt_variation");
  if (variations == null) {
    // No variation nothing to do
    return input;
  }
  var srcDoc = Repository.GetDocument(null, {"value": variations[0]});
  var duplicateProperties = ["inverbending:object", "inverbending:subject", "inverbending:transitivity", "inverbending:grammatical_mood", "inmorphem:kqt_variation"];
  for (var i in duplicateProperties) {
    input.setPropertyValue(duplicateProperties[i], srcDoc.getPropertyValue(duplicateProperties[i]));
  }
  DocumentMultivaluedProperty.addItem(input, {"value":srcDoc.getId(), "xpath": "inmorphem:kqt_variation", "save": false } );
  return input;
}

function run(input, params) {
  /**
  Will duplicate the document property
  **/
  var variations = input.getPropertyValue("inmorphem:kqt_variation");
  for (var i = 0; i < variations.length; i++) {
	  var srcDoc = Repository.GetDocument(null, {"value": variations[i]});
	  DocumentMultivaluedProperty.addItem(srcDoc, {"value":input.getId(), "xpath": "inmorphem:kqt_variation", "save": true } );
  }
  return input;
}

function run(input, params) {
  /**
  Will link phrase to morpheme
  **/
  var variations = input.getPropertyValue("inphrase:related_morpheme");
  if (variations != null) {
    for (var i = 0; i < variations.length; i++) {
      	 var srcDoc = Repository.GetDocument(null, {"value": variations[i]});
         if (srcDoc == undefined) continue;
         DocumentMultivaluedProperty.addItem(srcDoc, {"value":input.getId(), "xpath": "fv-word:related_phrases", "save": true } );
    }
  }
  return input;
}

function run(input, params) {
  /**
  Will link phrase to morpheme
  **/
      var links = input.getPropertyValue("inphrase:related_morpheme");
      for (var i = 0; i < links.length; i++) {
           var srcDoc = Repository.GetDocument(null, {"value": links[i]});
	   DocumentMultivaluedProperty.removeItemItem(srcDoc, {"value":input.getId(), "xpath": "fv-word:related_phrases", "save": true } );
      }
  return input;
}

function run(input, params) {
  /**
  Will link phrase to morpheme
  **/
  var originalDoc = Repository.GetDocument(null, {"value": input.getId()});
  var originalValues = [];
  if (originalDoc != null) {
      var links = originalDoc.getPropertyValue("inphrase:related_morpheme");
      if (links != null) {
        for (var i = 0; i < links.length; i++) {
      	  originalValues.push(links[i]);
        }
      }
  }
  var variations = input.getPropertyValue("inphrase:related_morpheme");
  if (variations != null) {
    for (var i = 0; i < variations.length; i++) {
          var ind = originalValues.indexOf(variations[i]);
          if (ind < 0) {
          	var srcDoc = Repository.GetDocument(null, {"value": variations[i]});
            	if (srcDoc == undefined) continue;
          	DocumentMultivaluedProperty.addItem(srcDoc, {"value":input.getId(), "xpath": "fv-word:related_phrases", "save": true } );
          } else {
              originalValues.splice(ind, 1);
          }
    }
  }
  for (var i = 0; i < originalValues.length; i++) {
	var srcDoc = Repository.GetDocument(null, {"value": originalValues[i]});
    	if (srcDoc == undefined) continue;
        Document.RemoveEntryOfMultivaluedProperty(srcDoc, {"value":input.getId(), "xpath": "fv-word:related_phrases", "save": true } );
  }
  return input;
}
